import { Component } from '@angular/core';
import { FormBuilder, Validators } from '@angular/forms';
import { NgbModal } from '@ng-bootstrap/ng-bootstrap';
import { UserprofileComponent } from '../userprofile/userprofile.component';
@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent {

  ProfileDP = "";
  age: number = 0;
  interest: string = "";
  firstName: string = "";
  LastName: string = "";
  Email: string = "";
  telno: any = "";
  State: string = "";
  Country: string = "";
  tags: string = "";
  address: string = "";
  value: number = 0;
  formBuilder: any;
  registrationForm: any;
  registerForm: any;

  onFileSelected(event: any) {

  }

  updateTextInput(e: any) {
    this.value = e.target.value;
  }
  constructor(private modalService: NgbModal, formBuilder: FormBuilder) {
    this.registerForm = this.formBuilder.group ({
      FirstName: ['', Validators.required],
      LastName: ['', Validators.required],
      password: ['', Validators.required],
      confirmPassword: ['', Validators.required],
   });
  }

  openUserprofileModal(){
    const modalRef = this.modalService.open(UserprofileComponent);
    modalRef.componentInstance.name = 'Cancel'
    modalRef.componentInstance.name = 'submit'


  }


}


export { UserprofileComponent };

